import React, { useState, useEffect } from 'react';
import './App.css';

function App() {
  const [startDate, setStartDate] = useState('');
  const [selectedUnit, setSelectedUnit] = useState(null);
  const [timeElapsed, setTimeElapsed] = useState({
    seconds: 0,
    minutes: 0,
    hours: 0,
    days: 0,
    weeks: 0,
    months: 0,
    anniversaries: 0
  });
  const [totalTime, setTotalTime] = useState({
    seconds: 0,
    minutes: 0,
    hours: 0,
    days: 0,
    weeks: 0,
    months: 0,
    anniversaries: 0
  });

  // Calculate time differences
  const calculateTimeElapsed = (start) => {
    const now = new Date();
    const startTime = new Date(start);
    const diffInMs = now - startTime;
    
    if (diffInMs < 0) return null;

    const seconds = Math.floor(diffInMs / 1000);
    const minutes = Math.floor(seconds / 60);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);
    const weeks = Math.floor(days / 7);
    
    // Calculate months (approximate)
    const months = Math.floor(days / 30.44); // Average days per month
    
    // Calculate anniversaries (years)
    const anniversaries = Math.floor(days / 365.25); // Account for leap years

    const elapsed = {
      seconds: seconds % 60,
      minutes: minutes % 60,
      hours: hours % 24,
      days: days % 7,
      weeks: weeks % 4, // Approximate weeks per month
      months: months % 12,
      anniversaries: anniversaries
    };

    const total = {
      seconds: seconds,
      minutes: minutes,
      hours: hours,
      days: days,
      weeks: weeks,
      months: months,
      anniversaries: anniversaries
    };

    return { elapsed, total };
  };

  // Update time every second
  useEffect(() => {
    if (!startDate) return;

    const interval = setInterval(() => {
      const result = calculateTimeElapsed(startDate);
      if (result) {
        setTimeElapsed(result.elapsed);
        setTotalTime(result.total);
      }
    }, 1000);

    return () => clearInterval(interval);
  }, [startDate]);

  // Calculate initial time when startDate changes
  useEffect(() => {
    if (startDate) {
      const result = calculateTimeElapsed(startDate);
      if (result) {
        setTimeElapsed(result.elapsed);
        setTotalTime(result.total);
      }
    }
  }, [startDate]);

  const handleDateChange = (e) => {
    setStartDate(e.target.value);
    setSelectedUnit(null); // Reset selection when date changes
  };

  const handleUnitClick = (unit) => {
    setSelectedUnit(selectedUnit === unit ? null : unit);
  };

  const getCurrentDate = () => {
    const now = new Date();
    const year = now.getFullYear();
    const month = String(now.getMonth() + 1).padStart(2, '0');
    const day = String(now.getDate()).padStart(2, '0');
    return `${year}-${month}-${day}`;
  };

  return (
    <div className="app">
      <div className="container">
        <header className="header">
          <h1 className="title">üíï Motmot Counter üíï</h1>
          <p className="subtitle">Track your beautiful journey together</p>
        </header>

        <div className="date-input-section">
          <label htmlFor="start-date" className="date-label">
            When did you start dating?
          </label>
          <input
            type="date"
            id="start-date"
            value={startDate}
            onChange={handleDateChange}
            max={getCurrentDate()}
            className="date-input"
          />
        </div>

        {startDate && (
          <div className="counter-section">
            <div className="time-display">
              <div className="time-unit">
                <span className="number">{timeElapsed.anniversaries}</span>
                <span className="label">Anniversary{timeElapsed.anniversaries !== 1 ? 'ies' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.months}</span>
                <span className="label">Month{timeElapsed.months !== 1 ? 's' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.weeks}</span>
                <span className="label">Week{timeElapsed.weeks !== 1 ? 's' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.days}</span>
                <span className="label">Day{timeElapsed.days !== 1 ? 's' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.hours}</span>
                <span className="label">Hour{timeElapsed.hours !== 1 ? 's' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.minutes}</span>
                <span className="label">Minute{timeElapsed.minutes !== 1 ? 's' : ''}</span>
              </div>
              <div className="time-unit">
                <span className="number">{timeElapsed.seconds}</span>
                <span className="label">Second{timeElapsed.seconds !== 1 ? 's' : ''}</span>
              </div>
            </div>
            
            <div className="love-message">
              <p>Every moment with you is precious! üíñ</p>
            </div>
          </div>
        )}

        {!startDate && (
          <div className="welcome-message">
            <p>Choose your special date to start counting your beautiful journey together! üåü</p>
          </div>
        )}

        <div className="floating-hearts">
          <div className="heart">üíñ</div>
          <div className="heart">üíï</div>
          <div className="heart">üíó</div>
          <div className="heart">üíù</div>
          <div className="heart">üíò</div>
        </div>
      </div>
    </div>
  );
}

export default App;
